import moment from "moment";
import React, { useState } from "react";
import { TableCell } from "@material-ui/core";

const firstDayOfMonth = (dateObj) =>
  moment(dateObj).startOf("month").format("d");

export default function Calendar() {
  const weekdayshort = moment.weekdaysShort();
  const [currentDate, setCurrentDate] = useState(moment());

  const firstDay = firstDayOfMonth(currentDate);
  const emptyCellsBeforeMonth = Array(firstDay).fill(<TableCell />);

  let daysInMonth = [];
  for (let i = 1; i <= currentDate.daysInMonth(); i++) {
    daysInMonth.push(
      <TableCell key={i} className="calendar-day">
        {i}
      </TableCell>
    );
  }
  var totalSlots = [...emptyCellsBeforeMonth, ...daysInMonth];

  let rows = [];

  const counter = 0;
  while (totalSlots.length > 0) {
    const week = totalSlots.slice(counter, counter + 7);
    rows.push(week);
    counter += 7;
  }

  let daysinmonth = rows.map((d, i) => {
    return <tr>{d}</tr>;
  });

  let weekday = weekdayshort.map((day) => <th key={day}>{day}</th>);

  return (
    <div>
      <table className="calendar-day">
        <thead>
          <tr>{weekday}</tr>
        </thead>
        <tbody>{daysinmonth}</tbody>
      </table>
    </div>
  );
}
