import moment from "moment";
import React, { useState } from "react";
import { TableCell } from "@material-ui/core";

const firstDayOfMonth = (dateObj) =>
  moment(dateObj).startOf("month").format("d");

export default function Calendar() {
  const weekdayshort = moment.weekdaysShort();
  const [currentDate, setCurrentDate] = useState(moment());

  const firstDay = firstDayOfMonth(currentDate);
  const emptyCellsBeforeMonth = Array(firstDay).fill(<TableCell />);

  let daysInMonth = [];
  for (let d = 1; d <= currentDate.daysInMonth(); d++) {
    daysInMonth.push(
      <td key={d} className="calendar-day">
        {d}
      </td>
    );
  }
  let weekday = weekdayshort.map((day) => <th key={day}>{day}</th>);

  var totalSlots = [...blanks, ...daysInMonth];
  let rows = [];
  let cells = [];

  totalSlots.forEach((row, i) => {
    if (i % 7 !== 0) {
      cells.push(row); // if index not equal 7 that means not go to next week
    } else {
      rows.push(cells); // when reach next week we contain all td in last week to rows
      cells = []; // empty container
      cells.push(row); // in current loop we still push current row to new container
    }
    if (i === totalSlots.length - 1) {
      // when end loop we add remain date
      rows.push(cells);
    }
  });

  let daysinmonth = rows.map((d, i) => {
    return <tr>{d}</tr>;
  });

  return (
    <div>
      <table className="calendar-day">
        <thead>
          <tr>{weekday}</tr>
        </thead>
        <tbody>{daysinmonth}</tbody>
      </table>
    </div>
  );
}
