    const week = totalSlots.slice(counter, counter + 7);
    rows.push(week);
    counter += 7;
  }

  let weeks = rows.map((d, i) => {
    return <TableRow key={i}>{d}</TableRow>;
  });

  return (
    <div>
      <Table>
        <TableHead>
          <DaysHeader></DaysHeader>
        </TableHead>
        <TableBody>{weeks}</TableBody>
      </Table>
    </div>
  );
}
