import moment from "moment";
import React, { useState } from "react";
import { TableCell, Table } from "@material-ui/core";

const firstDayOfMonth = (dateObj) =>
  moment(dateObj).startOf("month").format("d");

export default function Calendar() {
  const [currentDate, setCurrentDate] = useState(moment());

  const weekdayOfFirst = firstDayOfMonth(currentDate);
  let emptyCellsBeforeMonth = [];
  for (let i = 0; i < weekdayOfFirst; i++) {
    emptyCellsBeforeMonth.push(<TableCell />);
  }

  let daysInMonth = [];
  for (let i = 1; i <= currentDate.daysInMonth(); i++) {
    daysInMonth.push(
      <TableCell key={i} className="calendar-day">
        {i}
      </TableCell>
    );
  }

  var totalSlots = [...emptyCellsBeforeMonth, ...daysInMonth];
  let rows = [];
  let counter = 0;
  while (counter < totalSlots.length) {
    const week = totalSlots.slice(counter, counter + 7);
    rows.push(week);
    counter += 7;
  }

  let daysinmonth = rows.map((d, i) => {
    return <tr>{d}</tr>;
  });

  let weekdayNames = moment
    .weekdaysShort()
    .map((day) => <TableHead key={day}>{day}</TableHead>);

  return (
    <div>
      <Table className="calendar-day">
        <TableHead>
          <TableRow>{weekdayNames}</TableRow>
        </TableHead>
        <TableBody>{daysinmonth}</TableBody>
      </Table>
    </div>
  );
}
